import { useState, useEffect } from 'react'
import axios from 'axios'

export const TodoPage = () => {
  const [toogle, setToogle] = useState(false)
  const [getTodo, setGetTodo] = useState('')
  const [data, setData] = useState({
    title: '',
    desc: '',
    priority: '',
    id: '',
    creationTime: new Date().toLocaleString(),
  })

  const clearForm = () => {
    setData({ title: '', desc: '', priority: '', id: '' })
  }

  const getTodos = async () => {
    try {
      const result = await axios({
        url: 'http://127.0.0.1:5000/getTodos',
        method: 'GET',
      })
      console.log(result.data)
      setGetTodo(result.data)
    } catch (error) {
      console.error(error.message)
    }
  }

  useEffect(() => {
    getTodos()
  }, [])

  const handleInputChange = (e) => {
    const { name, value } = e.target
    setData({ ...data, [name]: value })
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      await axios({
        url: 'http://127.0.0.1:5000/addTodo',
        method: 'POST',
        data,
      })
      clearForm()
      await getTodos()
    } catch (error) {
      console.error(error.message)
    }
  }

  // delete function
  const handleDelete = (id) => {
    try {
      axios({
        url: `http://127.0.0.1:5000/deleteTodo/${id}`,
        method: 'DELETE',
      })
      console.log(data)
      setGetTodo(getTodo.filter((value) => value.id !== id))
    } catch (error) {
      console.error(error.message)
    }
  }

  //update function
  const handleToogle = (id) => {
    setGetTodo(getTodo.filter((value) => value.id === id))
    setToogle(!toogle)
  }
  async function handleEditFormSubmit(id) {
    try {
      await axios({
        url: `http://127.0.0.1:5000/updateTodo/${id}`,
        method: 'POST',
        data,
      })
      setGetTodo(getTodo.filter((value) => value.id === id))
      handleUpdateTodo(data.title, data.desc, data.id)
      setToogle(!toogle)
    } catch (error) {
      console.error(error.message)
    }
  }
  const handleUpdateTodo = (id, updatedTodo) => {
    const update = getTodo.map((value) =>
      value.id === id ? updatedTodo : data,
    )
    setGetTodo(update)
  }
  //status complete
  const handleComplete = () => {}
  return (
    <div className="todo-page">
      <div className="tp-container">
        {/* getData form */}
        <form className="todo-form" onSubmit={handleSubmit}>
          <input
            className="todo-input"
            onInput={handleInputChange}
            type="text"
            name="title"
            value={data.title}
            required
            placeholder="Title"
          />
          <input
            className="todo-input"
            onInput={handleInputChange}
            type="text"
            name="desc"
            value={data.desc}
            required
            placeholder="Description"
          />
          <select
            className="todo-input"
            onChange={handleInputChange}
            name="priority"
            value={data.priority}
            required
          >
            <option value="">select priority...</option>
            <option value="1">alta</option>
            <option value="2">media</option>
            <option value="3">bassa</option>
          </select>
          <button className="btn-todo-page" type="submit">
            Add todo
          </button>
        </form>
      </div>
      <div className="todo-list">
        {getTodo &&
          getTodo.map((value, index) => (
            <div key={index} className="box-card">
              <div className="card-container">
                <h3 className="title-todo">{value.title}</h3>
                <p className="text-todo">{value.desc}</p>
                <p className="text-todo">{value.creationTime}</p>
                <p className="text-todo">Priority: {value.priority}</p>
                <div className="btn-container">
                  <button className="btn-todo-page" onClick={handleComplete}>
                    complete
                  </button>
                  <button
                    className="btn-todo-page"
                    onClick={() => handleToogle(value.id)}
                  >
                    update
                  </button>
                  <button
                    className="btn-todo-page"
                    onClick={() => handleDelete(value.id)}
                  >
                    delete
                  </button>
                </div>
              </div>
            </div>
          ))}
      </div>

      {/* Toogle update */}
      {toogle && (
        <div className="toogle-container">
          <div className="toogle-list">
            <h2>Update your Todo</h2>
            <form className="toogle-form" onSubmit={handleEditFormSubmit}>
              <input
                className="todo-input"
                type="text"
                name="title"
                value={data.title}
                onChange={handleInputChange}
              />
              <input
                className="todo-input"
                type="text"
                name="desc"
                value={data.desc}
                onChange={handleInputChange}
              />
              <select
                className="todo-input"
                name="priority"
                required
                value={data.priority}
                onChange={handleInputChange}
              >
                <option value="">select priority...</option>
                <option value="1">alta</option>
                <option value="2">media</option>
                <option value="3">bassa</option>
              </select>
              <button className="btn-todo-page" type="submit">
                update
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  )
}
