import { useState } from 'react'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'

export const TodoPage = () => {
  const navigate = useNavigate()
  const [getTodo, setGetTodo] = useState('')

  const [data, setData] = useState({
    title: '',
    desc: '',
    priority: '',
  })

  const getTodos = async () => {
    try {
      const result = await axios({
        url: 'http://127.0.0.1:5000/getTodos',
        method: 'GET',
      })
      const _info = {
        p1: { tot: 0, complete: 0, todo: 0 },
        p2: { tot: 0, complete: 0, todo: 0 },
        p3: { tot: 0, complete: 0, todo: 0 },
      }
      result.data.forEach((value) => {
        const inf = _info[`p${value.priority}`]
        if (value.status === 2) {
          inf.complete = inf.complete + 1
        } else {
          inf.todo = inf.todo + 1
        }
        inf.tot = inf.tot + 1
      })
      setInfo(_info)

      setGetTodo(result.data.filter((value) => value.status === 2))
    } catch (error) {
      console.error(error.message)
    }
  }

  useEffect(() => {
    getTodos()
  }, [])

  const handleInputChange = (e) => {
    const { name, value } = e.target
    setData({ ...data, [name]: value })
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      await axios({
        url: 'http://127.0.0.1:5000/addTodo',
        method: 'POST',
        data,
      })
      navigate('/')
    } catch (error) {
      console.error(error.message)
    }
  }

  return (
    <div className="todo-page">
      <div className="tp-container">
        <form onSubmit={handleSubmit}>
          <input
            onInput={handleInputChange}
            type="text"
            name="title"
            value={data.title}
            required
          />
          <input
            onInput={handleInputChange}
            type="text"
            name="desc"
            value={data.desc}
            required
          />
          <select
            onChange={handleInputChange}
            name="priority"
            value={data.priority}
            required
          >
            <option value="">select priority...</option>
            <option value="1">alta</option>
            <option value="2">media</option>
            <option value="3">bassa</option>
          </select>
          <button type="submit">Add todo</button>
        </form>
      </div>
    </div>
  )
}
