import { useState, useEffect } from 'react'
import change from '../Svg/change.svg'
import complete from '../Svg/complete.svg'
import axios from 'axios'
export const TodoPage = () => {
  const [updateId, setUpdateId] = useState(null)
  const [updateMode, setUpdateMode] = useState(false)
  const [getTodo, setGetTodo] = useState('')
  const [_getTodo, _setGetTodo] = useState('')
  const [data, setData] = useState({
    title: '',
    desc: '',
    priority: '',
    id: '',
    creationTime: new Date().toLocaleString(),
  })
  const clearForm = () => {
    setData({ title: '', desc: '', priority: '', id: '' })
  }
  const sortTodos = (todos) => {
    todos.sort((a, b) => {
      return (
        a.priority - b.priority ||
        new Date(b.creationTime) - new Date(a.creationTime)
      )
    })
    return todos
  }

  const formatDateTodo = (todos) => {
    return todos.map((value) => {
      return {
        ...value,
        creationTime: new Date(value.creationTime).toLocaleString(),
      }
    })
  }

  const getTodos = async () => {
    try {
      const result = await axios({
        url: 'http://127.0.0.1:5000/getTodos',
        method: 'GET',
      })
      const dataSorted = sortTodos(result.data)
      setGetTodo(formatDateTodo(dataSorted))
      _setGetTodo(formatDateTodo(dataSorted))
    } catch (error) {
      console.error(error.message)
    }
  }

  useEffect(() => {
    getTodos()
  }, [])

  // inizio getTodo
  const handleInputChange = (e) => {
    const { name, value } = e.target
    setData({ ...data, [name]: value })
  }
  const _createTodo = async () => {
    try {
      await axios({
        url: 'http://127.0.0.1:5000/addTodo',
        method: 'POST',
        data,
      })
      clearForm()
      await getTodos()
    } catch (error) {
      console.error(error.message)
    }
  }
  // fine getTodo

  //inizio addTodo function
  const _updateTodo = async () => {
    try {
      await axios({
        url: `http://127.0.0.1:5000/updateTodo/${updateId}`,
        method: 'POST',
        data,
      })
      clearForm()
      setUpdateMode(false)
      setUpdateId(null)
      await getTodos()
    } catch (error) {
      console.error(error.message)
    }
  }
  const handleSubmit = async (e) => {
    e.preventDefault()
    if (!updateMode) {
      await _createTodo()
    } else {
      await _updateTodo()
    }
  }
  //fine addTodo function

  // delete function
  const handleDelete = async (id) => {
    try {
      await axios({
        url: `http://127.0.0.1:5000/deleteTodo/${id}`,
        method: 'DELETE',
      })
      await getTodos()
    } catch (error) {
      console.error(error.message)
    }
  }
  // fine delete function

  //inizio update function
  const toggleUpdateMode = (id) => {
    setUpdateId(updateMode === false ? id : null)
    if (updateMode === false) {
      const todo = getTodo.filter((value) => value.id === id)
      _setGetTodo(sortTodos(todo))
      setData(todo[0])
    } else {
      _setGetTodo(getTodo)
      clearForm()
    }
    setUpdateMode(!updateMode)
  }

  const handleToggleComplete = async (id, status) => {
    try {
      await axios({
        url: `http://127.0.0.1:5000/updateTodo/${id}`,
        method: 'POST',
        data: {
          status: status === 1 ? 2 : 1,
        },
      })
      await getTodos()
    } catch (error) {
      console.error(error.message)
    }
  }
  // fine update function
  return (
    <>
      <div className="tp-container">
        {/* getData form */}
        <form className="todo-form" onSubmit={handleSubmit}>
          <input
            className="todo-input"
            onInput={handleInputChange}
            type="text"
            name="title"
            value={data.title}
            required
            placeholder="Title"
          />
          <input
            className="todo-input"
            onInput={handleInputChange}
            type="text"
            name="desc"
            value={data.desc}
            required
            placeholder="Description"
          />
          <select
            className="todo-input"
            onChange={handleInputChange}
            name="priority"
            value={data.priority}
            required
          >
            <option value="">select priority...</option>
            <option value="1">alta</option>
            <option value="2">media</option>
            <option value="3">bassa</option>
          </select>
          <button className="btn-todo-page" type="submit">
            {!updateMode ? 'addTodo' : 'update'}
          </button>
        </form>
        {/* fine form  */}
      </div>
      {/* inizio card */}
      <div className="todo-list">
        {_getTodo &&
          _getTodo.map((value, index) => (
            <div key={index} className="box-card">
              <div className="card-container">
                <h3 className="title-todo">{value.title}</h3>
                <p className="text-todo">{value.desc}</p>
                <p className="text-todo">{value.creationTime}</p>
                <p className="text-todo">Priority: {value.priority}</p>
                <p className="text-todo">Status: {value.status}</p>
                <div className="btn-container">
                  <button
                    className="btn-todo-page"
                    onClick={() => handleToggleComplete(value.id, value.status)}
                  >
                    {value.status === 1 ? (
                      <img src={complete} alt="" />
                    ) : (
                      'undo'
                    )}
                  </button>
                  <button
                    className="btn-todo-page"
                    onClick={() => toggleUpdateMode(value.id)}
                  >
                    <img src={change} alt="change todo" />
                    {updateMode ? 'cancel' : 'update'}
                  </button>

                  <svg
                    onClick={() => handleDelete(value.id)}
                    xmlns="http://www.w3.org/2000/svg"
                    viewBox="0 0 24 24"
                    fill="currentColor"
                    class="w-6 h-6"
                  >
                    <path
                      fill-rule="evenodd"
                      d="M16.5 4.478v.227a48.816 48.816 0 013.878.512.75.75 0 11-.256 1.478l-.209-.035-1.005 13.07a3 3 0 01-2.991 2.77H8.084a3 3 0 01-2.991-2.77L4.087 6.66l-.209.035a.75.75 0 01-.256-1.478A48.567 48.567 0 017.5 4.705v-.227c0-1.564 1.213-2.9 2.816-2.951a52.662 52.662 0 013.369 0c1.603.051 2.815 1.387 2.815 2.951zm-6.136-1.452a51.196 51.196 0 013.273 0C14.39 3.05 15 3.684 15 4.478v.113a49.488 49.488 0 00-6 0v-.113c0-.794.609-1.428 1.364-1.452zm-.355 5.945a.75.75 0 10-1.5.058l.347 9a.75.75 0 101.499-.058l-.346-9zm5.48.058a.75.75 0 10-1.498-.058l-.347 9a.75.75 0 001.5.058l.345-9z"
                      clip-rule="evenodd"
                    />
                  </svg>
                </div>
              </div>
            </div>
          ))}
      </div>
      {/* fine card */}
    </>
  )
}
