import React, { useEffect } from 'react'
import axios from 'axios'
import { useState } from 'react'

export const MainPage = () => {
  const [getTodo, setGetTodo] = useState('')
  const [info, setInfo] = useState({
    p1: { tot: 0, complete: 0, todo: 0 },
    p2: { tot: 0, complete: 0, todo: 0 },
    p3: { tot: 0, complete: 0, todo: 0 },
  })

  const getTodos = async () => {
    try {
      const result = await axios({
        url: 'http://127.0.0.1:5000/getTodos',
        method: 'GET',
      })
      const _info = {
        p1: { tot: 0, complete: 0, todo: 0 },
        p2: { tot: 0, complete: 0, todo: 0 },
        p3: { tot: 0, complete: 0, todo: 0 },
      }
      result.data.forEach((value) => {
        const inf = _info[`p${value.priority}`]
        if (value.status === 2) {
          inf.complete = inf.complete + 1
        } else {
          inf.todo = inf.todo + 1
        }
        inf.tot = inf.tot + 1
      })
      setInfo(_info)

      setGetTodo(result.data.filter((value) => value.status === 2))
    } catch (error) {
      console.error(error.message)
    }
  }

  useEffect(() => {
    getTodos()
  }, [])

  return (
    <>
      <h2 className="h2-page">MainPage</h2>
      <div className="prio-container">
        <p>INFO</p>
        <p>
          ALTA: {info.p1.tot} tot {info.p1.complete} completed {info.p1.todo}{' '}
          todo
        </p>
        <p>
          MEDIA: {info.p2.tot} tot, {info.p2.complete} completed,{info.p2.todo}{' '}
          todo
        </p>
        <p>
          BASSA: {info.p3.tot} tot, {info.p3.complete} completed,{info.p3.todo}{' '}
          todo
        </p>
      </div>
      <div className="todo-list">
        {getTodo &&
          getTodo.map((value, index) => (
            <div key={index} className="box-card">
              <div className="card-container">
                <h3 className="title-todo">{value.title}</h3>
                <p className="text-todo">{value.desc}</p>
                <div className="btn-container">
                  <button className="btn-todo-page">complete</button>
                  <button className="btn-todo-page">delete</button>
                  <button className="btn-todo-page">modify</button>
                </div>
              </div>
            </div>
          ))}
      </div>
    </>
  )
}
