import { Tab } from '@headlessui/react'
import { useState, useEffect } from 'react'
import axios from 'axios'

export const TabPriority = () => {
  const [getTodo, setGetTodo] = useState('')
  const [info, setInfo] = useState({
    p1: { tot: 0, complete: 0, todo: 0 },
    p2: { tot: 0, complete: 0, todo: 0 },
    p3: { tot: 0, complete: 0, todo: 0 },
  })
  const getTodos = async () => {
    try {
      const result = await axios({
        url: 'http://127.0.0.1:5000/getTodos',
        method: 'GET',
      })
      const _info = {
        p1: { tot: 0, complete: 0, todo: 0 },
        p2: { tot: 0, complete: 0, todo: 0 },
        p3: { tot: 0, complete: 0, todo: 0 },
      }
      result.data.forEach((value) => {
        const inf = _info[`p${value.priority}`]
        if (value.status === 2) {
          inf.complete = inf.complete + 1
        } else {
          inf.todo = inf.todo + 1
        }
        inf.tot = inf.tot + 1
      })
      setInfo(_info)

      setGetTodo(result.data.filter((value) => value.status === 2))
    } catch (error) {
      console.error(error.message)
    }
  }

  useEffect(() => {
    getTodos()
  }, [])
  return (
    <Tab.Group>
      <Tab.List className="flex flex-row content-center items-center ">
        <Tab
          className="basis-1/4 rounded-lg  text-sm font-medium leading-5 text-[#ffffff]
ring-white ring-opacity-60 ring-offset-2 ring-offset-blue-400 focus:outline-none focus:ring-2"
        >
          Alta
        </Tab>
        <Tab
          className="basis-1/4 rounded-lg  text-sm font-medium leading-5 text-[#ffffff]
ring-white ring-opacity-60 ring-offset-2 ring-offset-blue-400 focus:outline-none focus:ring-2"
        >
          Media
        </Tab>
        <Tab className="basis-1/4 text-[#ffffff]">Bassa</Tab>
      </Tab.List>
      <Tab.Panels>
        <Tab.Panel>
          tot: {info.p1.tot} <br />
          completed: {info.p1.complete} <br />
          todo: {info.p1.todo}
        </Tab.Panel>
        <Tab.Panel>
          tot: {info.p2.tot} <br />
          completed: {info.p2.complete} <br />
          todo: {info.p2.todo}
        </Tab.Panel>
        <Tab.Panel>
          tot: {info.p3.tot} <br />
          completed: {info.p3.complete} <br />
          todo: {info.p3.todo}
        </Tab.Panel>
      </Tab.Panels>
    </Tab.Group>
  )
}
