export const TodoPage = () => {
  const [getTodo, setGetTodo] = useState('')
  const [data, setData] = useState({
    title: '',
    desc: '',
    priority: '',
    id: '',
    creationTime: new Date().toLocaleString(),
  })
  const clearForm = () => {
    setData({ title: '', desc: '', priority: '', id: '' })
  }

  const getTodos = async () => {
    try {
      const result = await axios({
        url: 'http://127.0.0.1:5000/getTodos',
        method: 'GET',
      })
      console.log(result.data)
      setGetTodo(result.data)
    } catch (error) {
      console.error(error.message)
    }
  }

  useEffect(() => {
    getTodos()
  }, [])

  const handleInputChange = (e) => {
    const { name, value } = e.target
    setData({ ...data, [name]: value })
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      await axios({
        url: 'http://127.0.0.1:5000/addTodo',
        method: 'POST',
        data,
      })
      clearForm()
      await getTodos()
    } catch (error) {
      console.error(error.message)
    }
  }

  // delete function
  const handleDelete = (id) => {
    try {
      axios({
        url: `http://127.0.0.1:5000/deleteTodo/${id}`,
        method: 'DELETE',
      })
      console.log(data)
      setGetTodo(getTodo.filter((value) => value.id !== id))
    } catch (error) {
      console.error(error.message)
    }
  }

  return (
    <>
      <div className="todo-list">
        {getTodo &&
          getTodo.map((value, index) => (
            <div key={index} className="box-card">
              <div className="card-container">
                <h3 className="title-todo">{value.title}</h3>
                <p className="text-todo">{value.desc}</p>
                <p className="text-todo">{value.creationTime}</p>
                <p className="text-todo">Priority: {value.priority}</p>
                <div className="btn-container">
                  <button className="btn-todo-page">complete</button>
                  <button
                    className="btn-todo-page"
                    onClick={() => handleToogle(value.id)}
                  >
                    update
                  </button>
                  <button
                    className="btn-todo-page"
                    onClick={() => handleDelete(value.id)}
                  >
                    delete
                  </button>
                </div>
              </div>
            </div>
          ))}
      </div>
    </>
  )
}
