import { useState, useEffect } from 'react'
import axios from 'axios'

export const TodoPage = () => {
  const [getTodo, setGetTodo] = useState('')
  const [data, setData] = useState({
    title: '',
    desc: '',
    priority: '',
  })

  const clearForm = () => {
    setData({ title: '', desc: '', priority: '' })
  }
  const getTodos = async () => {
    try {
      const result = await axios({
        url: 'http://127.0.0.1:5000/getTodos',
        method: 'GET',
      })

      setGetTodo(result.data)
    } catch (error) {
      console.error(error.message)
    }
  }

  useEffect(() => {
    getTodos()
  }, [])

  const handleInputChange = (e) => {
    const { name, value } = e.target
    setData({ ...data, [name]: value })
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      await axios({
        url: 'http://127.0.0.1:5000/addTodo',
        method: 'POST',
        data,
      })
      await getTodos()
    } catch (error) {
      console.error(error.message)
    }
  }

  return (
    <div className="todo-page">
      <div className="tp-container">
        <form onSubmit={handleSubmit}>
          <input
            onInput={handleInputChange}
            type="text"
            name="title"
            value={data.title}
            required
          />
          <input
            onInput={handleInputChange}
            type="text"
            name="desc"
            value={data.desc}
            required
          />
          <select
            onChange={handleInputChange}
            name="priority"
            value={data.priority}
            required
          >
            <option value="">select priority...</option>
            <option value="1">alta</option>
            <option value="2">media</option>
            <option value="3">bassa</option>
          </select>
          <button type="submit">Add todo</button>
        </form>
      </div>
      <div className="todo-list">
        {getTodo &&
          getTodo.map((value, index) => (
            <div key={index} className="box-card">
              <div className="card-container">
                <h3 className="title-todo">{value.title}</h3>
                <p className="text-todo">{value.desc}</p>
                <div className="btn-container">
                  <button className="btn-todo-page">complete</button>
                  <button className="btn-todo-page">delete</button>
                </div>
              </div>
            </div>
          ))}
      </div>
    </div>
  )
}
