import { useState, useEffect } from 'react'
import axios from 'axios'

export const TodoPage = () => {
  const [getTodo, setGetTodo] = useState('')
  const [data, setData] = useState({
    title: '',
    desc: '',
    priority: '',
    id: '',
  })

  const clearForm = () => {
    setData({ title: '', desc: '', priority: '', id: '' })
  }

  const getTodos = async () => {
    try {
      const result = await axios({
        url: 'http://127.0.0.1:5000/getTodos',
        method: 'GET',
      })
      console.log(result.data)
      setGetTodo(result.data)
    } catch (error) {
      console.error(error.message)
    }
  }

  useEffect(() => {
    getTodos()
  }, [])

  const handleInputChange = (e) => {
    const { name, value } = e.target
    setData({ ...data, [name]: value })
  }

  const handleInputUpdate = (e) => {
    setData({
      ...data,
      title: e.target.title.value,
      desc: e.target.desc.value,
      priority: e.target.priority.value,
    })
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      await axios({
        url: 'http://127.0.0.1:5000/addTodo',
        method: 'POST',
        data,
      })
      clearForm()
      await getTodos()
    } catch (error) {
      console.error(error.message)
    }
  }

  const handleUpdate = (e) => {
    e.preventDefault()
    try {
      axios({
        url: `http://127.0.0.1:5000/updateTodo/${e.target.cards.value}`,
        method: 'POST',
      })
      setGetTodo(
        getTodo.filter((value) => {
          if (value.id === e.target.cards.value) {
            handleInputUpdate(e)
          }
        }),
      )
    } catch (error) {
      console.log(error.message)
    }
  }
  const handleDelete = (id) => {
    try {
      axios({
        url: `http://127.0.0.1:5000/deleteTodo/${id}`,
        method: 'DELETE',
      })
      console.log(data)
      setGetTodo(getTodo.filter((value) => value.id !== id))
    } catch (error) {
      console.error(error.message)
    }
  }

  return (
    <div className="todo-page">
      <div className="tp-container">
        {/* getData form */}
        <form className="todo-form" onSubmit={handleSubmit}>
          <input
            className="todo-input"
            onInput={handleInputChange}
            type="text"
            name="title"
            value={data.title}
            required
            placeholder="Title"
          />
          <input
            className="todo-input"
            onInput={handleInputChange}
            type="text"
            name="desc"
            value={data.desc}
            required
            placeholder="Description"
          />
          <select
            className="todo-input"
            onChange={handleInputChange}
            name="priority"
            value={data.priority}
            required
          >
            <option value="">select priority...</option>
            <option value="1">alta</option>
            <option value="2">media</option>
            <option value="3">bassa</option>
          </select>
          <button className="btn-todo-page" type="submit">
            Add todo
          </button>
        </form>

        {/* update form */}

        <form className="todo-form" onSubmit={(e) => handleInputUpdate(e)}>
          <input
            className="todo-input"
            onInput={handleInputUpdate}
            type="text"
            name="title"
            value={data.title}
            required
            placeholder="Title"
          />
          <input
            className="todo-input"
            onInput={handleInputUpdate}
            type="text"
            name="desc"
            value={data.desc}
            required
            placeholder="Description"
          />
          <select name="cards" id="">
            <option value="">select card...</option>
            {getTodo &&
              getTodo.map((value) => (
                <option key={value.title} value={value.id}>
                  {value.id}
                </option>
              ))}
          </select>
          <select
            className="todo-input"
            onChange={handleInputUpdate}
            name="priority"
            value={data.priority}
            required
          >
            <option value="">select priority...</option>
            <option value="1">alta</option>
            <option value="2">media</option>
            <option value="3">bassa</option>
          </select>
          <button
            className="btn-todo-page"
            type="submit"
            onClick={() => handleUpdate()}
          >
            update
          </button>
        </form>
      </div>
      <div className="todo-list">
        {getTodo &&
          getTodo.map((value, index) => (
            <div key={index} className="box-card">
              <div className="card-container">
                <h3 className="title-todo">{value.title}</h3>
                <p className="text-todo">{value.desc}</p>
                <p className="text-todo">Priority: {value.priority}</p>
                <div className="btn-container">
                  <button className="btn-todo-page">complete</button>
                  {/* <button className="btn-todo-page">update</button> */}
                  <button
                    className="btn-todo-page"
                    onClick={() => handleDelete(value.id)}
                  >
                    delete
                  </button>
                </div>
              </div>
            </div>
          ))}
      </div>
    </div>
  )
}
